sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

	var NowPriority = g_form.getValue('priority');
		jslog('優先度の現在の値 ' + NowPriority);

		g_form.clearOptions('priority');

	if(newValue == 'facilities2'||newValue == 'hr2'||newValue == 'legal2'){
		g_form.addOption('priority',1,'1 絶対やって');
		g_form.addOption('priority',2,'2 暇なときやって');
		g_form.addOption('priority',3,'3 やらなくていい');

	}else{
		g_form.addOption('priority',4,'4 申請しといて');
		g_form.addOption('priority',5,'5 やらんかも');
	}
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('priority',NowPriority);
	}





   //Type appropriate comment here, and begin script below

}
